macro.script:
if(!checkData.action)
	return false;

const action = await fromUuid(checkData.action),
	  traits = action.system[checkData.face].traits;
console.log(action, game.i18n.localize("ACTION.TRAITS.rankOne"))
if(action.system.type === "spell" && (traits.includes(game.i18n.localize("TRAITS.rankZero")) || traits.includes(game.i18n.localize("TRAITS.rankOne"))))
	diePool.dice.fortune++;
