macro.script:
if(game.user.targets.length !== 1)
  return ui.notifications.warn(game.i18n.localize("ABILITY.WARNINGS.oneTargetRequired"));

const attackQuarryEffect = await fromUuid(".ActiveEffect.OeEnLPUpOBN6E1EY", {relative: this.parent});

await attackQuarryEffect.setFlag("wfrp3e", "quarry", game.user.targets[0].document.actor.uuid);
await attackQuarryEffect.update({"active": true});

await this.parent.exhaust();
