system.script:
const skills = await Promise.all([
      await fromUuid("Compendium.wfrp3e.items.Item.0BSYtxTPqR72Tfuq"),
      await fromUuid("Compendium.wfrp3e.items.Item.LjSfvJxmSzqkU7NV"),
      await fromUuid("Compendium.wfrp3e.items.Item.xIkIYMUHDtUrqjY8")
]),
      buttons = [];

for(const skill of skills)
  buttons.push({
    action: skill._id,
    label: skill.name,
    callback: (event, button, dialog) => skill.uuid
  });

options.freeTrainings.push(
  await foundry.applications.api.DialogV2.wait({
    window: {title: this.name},
    modal: true,
    content: this.parent.system.description,
    buttons
  })
);
