macro.script:
const action = await fromUuid(checkData.action),
      skill = await fromUuid(checkData.skill);
if(skill?.name === game.i18n.localize("SKILL.NAMES.guile") && action?.system[checkData.face].traits.includes(game.i18n.localize("TRAITS.social")))
  checkRoll.effects?.boon.push({
    description: "<p>The target must make an Average (2d) Discipline (WP) check or gain the Rattled condition for 2 rounds</p>",
    immediate: false,
    reverseScript: "",
    script: "const discipline = targetActor.itemTypes.skill.find(skill => skill.name === game.i18n.localize(\"SKILL.NAMES.discipline\")),\n      check = discipline\n\t? await targetActor.prepareSkillCheck(discipline)\n\t: await targetActor.prepareCharacteristicCheck(\"willpower\");\nif(!check.checkRoll.totalSymbols.successes)\n\tawait targetActor.createEmbeddedDocuments(\"Item\", [await fromUuid(\"Compendium.wfrp3e.items.Item.CSck5rRPkGyRad5B\")])",
    symbolAmount: 2
  });
