macro.script:
if(!checkData.action)
	return false;

const action = await fromUuid(checkData.action),
	  traits = action.system[checkData.face].traits;
if(action.system.type === "blessing" && (traits.includes(game.i18n.localize("TRAITS.rankZero")) || traits.includes(game.i18n.localize("TRAITS.rankOne"))))
	diePool.dice.fortune++;
