macro.script:
const skills = await Promise.all([
      await fromUuid("Compendium.wfrp3e.items.ytY395HObq47bOhq"),
      await fromUuid("Compendium.wfrp3e.items.wjAWNmMw9CMwEuZm"),
      await fromUuid("Compendium.wfrp3e.items.PVY38LVHfi4ZxsI6"),
      await fromUuid("Compendium.wfrp3e.items.Item.J7ySozk1SB3VwGxl")
]),
      buttons = [];

for(const skill of skills)
  buttons.push({
    action: skill._id,
    label: skill.name,
    callback: (event, button, dialog) => skill.uuid
  });

options.freeTrainings.push(
  await foundry.applications.api.DialogV2.wait({
    window: {title: this.name},
    modal: true,
    content: this.parent.system.description,
    buttons
  })
);
