const actionUuids = await ActionSelector.wait({items: actor.itemTypes.action.filter(action => action.system.reckless.traits.includes(game.i18n.localize("TRAITS.social")))});
if(actionUuids) {
  await actionUuids[0].update({"system.rechargeTokens": 0});
  await this.parent.exhaust();
}
