macro.script:
const rechargingItems = actor.items.filter(item => item.system.rechargeTokens > 0);
if(rechargingItems.length) {
  const uuids = await wfrp3e.applications.apps.selectors.ItemSelector.wait({items: rechargingItems});
  if(uuids.length) {
    const item = await fromUuid(uuids[0]);
    await item.adjustRechargeTokens(-2);
  }
}
