macro.script:
await foundry.applications.api.DialogV2.confirm({
  content: item.system.description,
  window: {title: item.name},
  submit: async result => {
    if(result) {
      actor.system.soakModifier += 2;
	foundry.utils.mergeObject(actor, {"overrides.system.soakModifier": actor.system.soakModifier});
      await this.parent.exhaust();
    }
  }
});
