macro.script:
const skill = await fromUuid(checkData.skill);
if(skill?.name === game.i18n.localize("SKILL.NAMES.education"))
  checkRoll.effects?.boon.push({
    description: '<p>Add 1 fortune to the party fortune pool</p>',
    immediate: false,
    reverseScript: "",
    script: "await actor.system.currentParty?.adjustFortune(1);",
    symbolAmount: 2
  });
