macro.script:
const slayerTrait = game.i18n.localize("TRAITS.slayer");
if(actor.system.currentCareer.system.traits.includes(slayerTrait)) {
	actor.system.defenceValue = 1;
	actor.system.soakValue = 1;
	for(const career of actor.itemTypes.career)
		if(career.system.traits.includes(slayerTrait) && career.system.dedicationBonus)
			actor.system.soakValue++;

	foundry.utils.mergeObject(actor.overrides, {system: {defenceValue: actor.system.defenceValue, soakValue: actor.system.soakValue}});
}
