const drawnResult = await rollTable.drawMany(2, {roll, recursive: false, displayChat, rollMode});

for(const index in drawnResult.results) {
  let result = drawnResult.results[index], document = await fromUuid(result.documentUuid);

  while(document.documentName !== "Item") {
    const newResult = await rollTable.draw({roll, recursive: false, displayChat, rollMode});
    drawnResult.results.splice(index, 1, newResult.results[0]);
    result = drawnResult.results[index];
    document = await fromUuid(result.documentUuid);
  }
}

return drawnResult;
